import '../App.css'



export default class Serch extends Component {
constructor(props){
    super(props);
}

handleChange = (e) =>{
    this.props.onChercheChange(e.target.value)
}
    render() {
        const cherche = this.props.cherche
        return (
            <div>
                <input type="text" value={cherche} onChange={this.handleChange} />
            </div>
        )
    }
}
