import React, { Component } from "react";
import axios from "axios";

import "../App.css";

class Test extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      stri: 0,
    };
  }

  getData() {
    axios.get("movie.json").then((response) => {
      var a = response.data;
      this.setState({ data: a });
    });
  }
  componentDidMount() {
    this.getData();
  }

  chage = (e) => {
    this.setState({
      stri: e.target.value
    });
  };

  render() {
    return (
      <div>


        <input type="text" list="browsers" name="browser" onChange={this.chage}  />

    <div>

        {this.state.data.filter((x) => x.id.includes(this.state.stri)).map((el) => (
              <div className="serch-drop">

                    <img src={el.poster} alt="tv" className="serch-img" />

                      <h2 className="serch-">{el.title}</h2>



               <hr/>
                </div>

            ))}
      </div></div>
    );
  }
}

export default Test;
