import React,{useState,useEffect} from 'react'
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Desc from "./components/desc.js";
import Apropo from "./components/apropo.js";
import Trending from "./components/trending.js";
import Footer from "./components/footer.js";
import Nava from './components/Navbar.js'
import { BrowserRouter, Route } from "react-router-dom";
import axios from 'axios'
import Home from './components/Home'
import Favourite from './components/favourite'
function App() {
  const [pfav, setPfav] = useState([]);
  const [fav, setFav] = useState(0);
  const getFav = () => {
    setFav(fav + 1);
  };

  const getpfav = (el) => {
    pfav.push(el);
  };
  const [movie, setMovie] = useState([]);
  useEffect(() => {
    axios.get(`http://localhost:3004/movie`).then(res => {
      var a = res.data;
      setMovie({a});
    });
  }, []);

  return (

 <div className="ap">
<BrowserRouter >
<Route exact  path="/" >
<Home movie={movie} getFav={getFav} getpfav={getpfav} fav={fav} />
</Route>
<Route exact path="/Favourite" >
          <Favourite movie={movie} pfav={pfav}   />
        </Route>
</BrowserRouter>
<Footer />
    </div>
  );
}

export default App;
