import React, { Component } from "react";
import axios from "axios";

import "../App.css";

class Test extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      stri: 0,
    };
  }

  getData() {
    axios.get("movie.json").then((response) => {
      var a = response.data;
      this.setState({ data: a });
    });
  }
  componentDidMount() {
    this.getData();
  }

  chage = (e) => {
    this.setState({
      stri: e.target.value
    });
  };

  render() {
    return (
      <div>


      <form action="/action_page.php">
        <input type="text" list="browsers" name="browser" onChange={this.chage}  />








        {this.state.data.filter((x) => x.id.includes(this.state.stri)).map((el) => (
          <datalist id="browsers">

              <option>
              <div className="background_6">
                <div className="imdb_card">
                  <div className="movie_poster">
                    <img src={el.poster} alt="tv" />
                    <div className="movie_label ">
                      <h2 className="">{el.title}</h2>
                    </div>
                  </div>

                  <div className="movie_content">
                    <div className="mov_specs ">
                      <p>8.5 | 1h 57min | Animation, Action, Adventure</p>
                    </div>
                    <div className="mov_description d-flex justify-content-center  ">
                      <span className="fa fa-star checked "></span>
                      <span className="fa fa-star checked"></span>
                      <span className="fa fa-star checked"></span>
                      <span className="fa fa-star checke "></span>
                      <span className="fa fa-star checke "></span>
                    </div>
                    <div className="buy_ticket">
                      <a >
                        <span>WATCH NOW</span>

                        <span>
                          <svg
                            xmlns="http://www.w3.org/2000/svg"
                            className="mt-1"
                            width="25"
                            height="25"
                            fill="currentColor"
                            class="bi bi-heart-fill"
                            viewBox="0 0 16 16"
                          >
                            <path
                              fill-rule="evenodd"
                              d="M8 1.314C12.438-3.248 23.534 4.735 8 15-7.534 4.736 3.562-3.248 8 1.314z"
                            />
                          </svg>
                        </span>
                      </a>
                    </div>
                  </div>
                </div>
              </div></option></datalist>
            ))}
       </form>
      </div>
    );
  }
}

export default Test;
