import React,{useState,useEffect} from 'react'
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Footer from "./components/footer.js";
import { BrowserRouter, Route } from "react-router-dom";
import axios from 'axios'
import Home from './components/Home'
import Favourite from './components/favourite'
import Dashp from './dashbord/dash'

function App() {
  const [pfav, setPfav] = useState([]);
  const [fav, setFav] = useState(0);
  const getFav = () => {
    setFav(fav + 1);
  };

  const getpfav = (el) => {
    pfav.push(el);
  };
  const [movie, setMovie] = useState([]),
  resul = () => {
    axios.get(`https://movie-app-57731-default-rtdb.firebaseio.com/movie`).then(res => {
      var a = res.data;
      setMovie(a);
  })};
useEffect(() => {
  resul();

}, []); 
const delta = (id) => {
  axios.delete(`https://movie-app-57731-default-rtdb.firebaseio.com/${id}`).then((res) => {
    console.log(res.data);
  });
};
useEffect(() => {
delta()
}, []);
const [data, setData] = useState({
  title: "",
  genres: "",
  poster:null
});
const sub = (event) => {
  event.preventDefault();

  axios.post(`https://movie-app-57731-default-rtdb.firebaseio.com/`, data).then((res) => {
    console.log(res);
    console.log(res.data);
  });
};
  return (

 <div className="ap">
<BrowserRouter >
<Route exact  path="/" >
<Home movie={movie} getFav={getFav} getpfav={getpfav} fav={fav} />
</Route>
<Route exact path="/Favourite" >
          <Favourite movie={movie} pfav={pfav} delta={delta}  />
        </Route>
        <Route exact path="/Admin" >
        <Dashp sub={sub} setData={setData} movie={movie}  data={data} delta={delta} />
        </Route>
</BrowserRouter>
<Footer />
    </div>
  );
}

export default App;
