import React, { Component } from 'react'
import axios from "axios";

export default class Filter extends Component {
    constructor(props) {
        super(props);
        this.state = {
          data: [],
          stri: 0,
        };
      }

      getData() {
        axios.get("film.json").then((response) => {
          var a = response.data;
          this.setState({ data: a });
        });
      }
      componentDidMount() {
        this.getData();
      }

      chage = (e) => {
        if (document.getElementById("ly").value == "") {
          this.setState({
            stri: null,
          });
        } else {
          this.setState({
            stri: document.getElementById("ly").value,
          });
        }
      };

    render() {
        return (
            <div>

     <div>

<ul className="ul" id="yl">
  <a onClick={this.chage} value="comedy">Comedy</a>
  <a value="crime">Crime</a>
  <a value="adventure">Adventure</a>
  <a value="drama">Drama</a>
  <a value="romance">Romance</a>
  <a value="animation">Animation</a>
  <a value="family">Family</a>
  <a value="science Fiction">Science Fiction</a>
  <a value="action">Action</a>

</ul>
</div>
        {this.state.data
          .filter((x) => x.genres.includes(this.state.stri))
          .map((el) => ( 

            <div className="background_6">
            <div className="imdb_card">
              <div className="movie_poster">
                <img src={el.poster} alt="tv" />
                <div className="movie_label ">
                  <h2 className="">{el.title}</h2>
                </div>
              </div>

              <div className="movie_content">
                <div className="mov_specs ">
                  <p>8.5 | 1h 57min | Animation, Action, Adventure</p>
                </div>
                <div className="mov_description d-flex justify-content-center  ">
                  <span className="fa fa-star checked "></span>
                  <span className="fa fa-star checked"></span>
                  <span className="fa fa-star checked"></span>
                  <span className="fa fa-star checke "></span>
                  <span className="fa fa-star checke "></span>
                </div>
                <div className="buy_ticket">
                  <a OnClick="">
                    <span>WATCH NOW</span>

                    <span>
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="mt-1"
                        width="25"
                        height="25"
                        fill="currentColor"
                        class="bi bi-heart-fill"
                        viewBox="0 0 16 16"
                      >
                        <path
                          fill-rule="evenodd"
                          d="M8 1.314C12.438-3.248 23.534 4.735 8 15-7.534 4.736 3.562-3.248 8 1.314z"
                        />
                      </svg>
                    </span>
                  </a>
                </div>
              </div>
            </div>
          </div>
          ))}
            </div>
        )
    }
}
