import React, { Component } from "react";
import axios from "axios";
import { Navbar, Nav, Form } from "react-bootstrap";
import "../App.css";
import { Link } from "react-router-dom";


class Nava extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      stri: 0,
    };
  }

  getData() {
    axios.get("film.json").then((response) => {
      var a = response.data;
      this.setState({ data: a });
    });
  }
  componentDidMount() {
    this.getData();
  }

  chage = (e) => {
    if (e.target.value == "") {
      this.setState({
        stri: null,
      });
    } else {
      this.setState({
        stri: e.target.value,
      });
    }
  };

  render() {
    return (
      <div>
        <Navbar className="nava " variant="dark">
          <Navbar.Brand href="#home">
            <img src="/imgs/logi.png" className="logi " alt="logi" />
          </Navbar.Brand>

          <Form className="mx-auto  ">
            <input
              type="text"
              onChange={this.chage}
              id="serch"
              placeholder="Search"
            />
          </Form>
          <Nav>
            <Nav.Link href="#home" className="text-white mx-2 ">
              Acceuil
            </Nav.Link>

            <Link href="#pricing" className="text-white mx-2">
              Contact
            </Link>
          </Nav>


        </Navbar>

        {this.state.data
          .filter((x) => x.id.includes(this.state.stri))
          .map((el) => (
            <div>
              <div id="serch-drop">
                <img src={el.poster} alt="tv" className="serch-img  " />
                <h2 className="serch-tit">{el.title}</h2>
              </div>

              <br />
            </div>
          ))}
      </div>
    );
  }
}

export default Nava;
