import React, { Component } from "react";
import axios from "axios";
import { Navbar, Nav, } from "react-bootstrap";
import "../App.css";

class Fela extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      stri: 0,
    };
  }

  getData() {
    axios.get("movie.json").then((response) => {
      var a = response.data;
      this.setState({ data: a });
    });
  }
  componentDidMount() {
    this.getData();
  }


  chage = (e) => {
    if(e.target.value == ""){
        this.setState({
      stri: null
    });
}else{

    this.setState({
      stri: e.target.value 
    });}
  };

  render() {
    return (
        <div className="hsin">
                <input type="text" onChange={this.chage}   className="mx-auto "id="serch" placeholder="Search" />

        {this.state.data.filter((x) => x.id.includes(this.state.stri)).map((el) => (
              <div id="serch-drop">

                    <img src={el.poster} alt="tv" className="serch-img " />

                      <h2 className="serch-tit">{el.title}</h2>



               <hr/>
                </div>

            ))}
      </div>
    );
  }
}

export default Fela;
